DROP  database IF EXISTS  flm;

create database flm;

use flm;

CREATE TABLE Account(
	accountId int auto_increment primary key not null,
	userName varchar(150) not null,
	password varchar(150) not null,
	phone bigint not null,
	address nvarchar(200) not null,
	gender int not null,
	gmail varchar(150) not null,
	fullName nvarchar(150) not null,
    image varchar(150) not null,
	birthday date not null,
	roleId int not null,
    isActive tinyint not null
);


CREATE TABLE Role(
	roleId int auto_increment primary key not null,
	roleName varchar(150) not null
);

CREATE TABLE Subject(
	subjectCode varchar(50) primary key not null,
	subjectNameVn nvarchar(255) not null,
	subjectNameEn varchar(255) not null,
	semester varchar(25) not null,
	noCredit int not null,
	preRequisite varchar(3000) not null
);

CREATE TABLE Syllabus(
	syllabusId int auto_increment primary key not null,
	subjectCode varchar(50) not null,
	syllabusNameVn nvarchar(255) not null,
	syllabusNameEn varchar(255) not null,
	degreeLevel nvarchar(50) not null,
	timeAllocation nvarchar(255) not null,
	description nvarchar (3000) not null,
	studentTask nvarchar(3000) not null,
	tool nvarchar(255) not null,
	isApproved bit not null,
	note nvarchar(3000) not null,
	minAvgMarkToPass int not null,
	isActive bit not null,
	approvedDate date not null,
	decisionId INT not null
);


CREATE TABLE Material(
	materialId int auto_increment primary key not null,
	materialDescription nvarchar(3000) not null,
	author nvarchar(255) not null,
	publisher nvarchar(255) not null,
	publishedDate date,
	edition nvarchar(255) not null,
	ISBN nvarchar(150) not null,
	isMainMaterial bit not null,
	isHardCopy bit not null,
	isOnline bit not null,
	note nvarchar(3000) not null,
	subjectCode varchar(50) not null
);


CREATE TABLE LO( 
	loId int auto_increment primary key not null,
	cloName varchar(50) not null,
	cloDetails varchar(3000) not null,
	loDetails varchar(3000) not null
);


CREATE TABLE Session(
	sessionId int auto_increment primary key not null,
	sessionNo varchar(50) not null,
	topic varchar(255) not null,
	learningTeachingType varchar(50) not null,
	itu varchar(50) not null,
	studentMaterials nvarchar(255) not null,
	sDownload nvarchar(255) not null,
	studentTasks nvarchar(255) not null,
	urls varchar(255) not null,
	subjectCode varchar(50) not null
);


CREATE TABLE Question(
	questionId int auto_increment primary key not null,
	name varchar(255) not null,
	detail nvarchar(3000) not null,
	sessionId int not null
);


CREATE TABLE Assessment(
	assessmentId int auto_increment primary key not null,
	category nvarchar(256) not null,
	type varchar(150) not null,
	part varchar(150) not null,
	weight varchar(50) not null,
	completionCriteria varchar(256) not null,
	duration nvarchar(255) not null,
    clo varchar(255) not null,
	questionType nvarchar(255) not null,	
	noQuestion nvarchar(255) not null,
	knowledgeSkill nvarchar(255) not null,	
	gradingGuide nvarchar(3000) not null,	
	note nvarchar(3000) not null,
	subjectCode varchar(50) not null
);


CREATE TABLE Decision(
	decisionId int auto_increment primary key not null,
	decisionNo varchar(150) not null,
	decisionName nvarchar(256) not null,
	approvedDate date not null,
	note nvarchar(3000) not null,
	createDate date not null,
	fileName varchar(150) not null,
    isActive bit,
    isApprove bit
);


CREATE TABLE Curriculum(
	curriculumId int auto_increment primary key not null,
	curriculumCode varchar(50) not null,
	nameVn nvarchar(255) not null,
	nameEn varchar(255) not null,
	decription nvarchar(3000) not null,
	decisionId int not null
);


CREATE TABLE PLO(
	ploId int auto_increment primary key not null,
	ploName varchar(50) not null,
	ploDecription varchar(3000) not null,
	curriculumId int not null
);


CREATE TABLE PO(
	poId int primary key not null,
	poName varchar(50) not null,
	poDecription varchar(3000) not null,
	curriculumId int not null
);


CREATE TABLE Curriculum_Subject(
	curriculumId int not null,
	subjectCode varchar(50) not null,
	primary key(curriculumId, subjectCode)
);


CREATE TABLE Combo(
	comboId int auto_increment primary key not null,
	comboNameVn nvarchar(255) not null,
	comboNameEn varchar(255) not null,
	note nvarchar(3000) not null,
	tag varchar(50) not null,
	curriculumId int not null
);


CREATE TABLE Elective(
	electiveId int auto_increment primary key not null,
	electiveNameVn nvarchar(255) not null,
	electiveNameEn varchar(255) not null,
	note nvarchar(3000) not null,
	tag varchar(50) not null,
	curriculumId int not null
);


CREATE TABLE Combo_Subject(
	comboId int not null,
	subjectCode varchar(50) not null,
	semester varchar(25) not null,
	primary key(comboId, subjectCode)
);


CREATE TABLE Elective_Subject(
	electiveId int not null,
	subjectCode varchar(50) not null,
	primary key(electiveId, subjectCode)
);

CREATE TABLE Feature(
	featureId int primary key not null,
    featureName varchar(50) not null,
    url varchar(50) not null
);

CREATE TABLE GoogleLoginHistory(
	id varchar(150) primary key not null,
    email varchar(50) not null,
    timeLogin date not null
);

CREATE TABLE Role_Feature(
	roleId int not null,
    featureId int not null,
    PRIMARY KEY(roleId, featureId)
);

ALTER TABLE Role_Feature ADD CONSTRAINT fk_Role_Feature_Role foreign key(roleId) references Role(roleId),
ADD CONSTRAINT fk_Role_Feature_Feature foreign key(featureId) references Feature(featureId);

ALTER TABLE Combo_Subject ADD CONSTRAINT fk_Combo_Subject_Subject foreign key(subjectCode) references Subject(subjectCode), 
ADD CONSTRAINT fk_Combo_Subject_Combo foreign key(comboId) references Combo(comboId);

ALTER TABLE Elective_Subject ADD CONSTRAINT fk_Elective_Subject_Subject foreign key(subjectCode) references Subject(subjectCode), 
ADD CONSTRAINT fk_Elective_Subject_Elecitve foreign key(electiveId) references Elective(electiveId);

ALTER TABLE Combo ADD CONSTRAINT fk_Combo_Curriculum foreign key(curriculumId) references Curriculum(curriculumId);

ALTER TABLE Elective ADD CONSTRAINT fk_Elective_Curriculum foreign key(curriculumId) references Curriculum(curriculumId);

ALTER TABLE Curriculum_Subject ADD CONSTRAINT fk_Curriculum_Subject_Curriculum foreign key(curriculumId) references Curriculum(curriculumId), 
ADD CONSTRAINT fk_Curriculum_Subject_Subject foreign key(subjectCode) references Subject(subjectCode);

ALTER TABLE PO ADD CONSTRAINT fk_PO_Curriculum foreign key(curriculumId) references Curriculum(curriculumId);

ALTER TABLE PLO ADD CONSTRAINT fk_PLO_Curriculum foreign key(curriculumId) references Curriculum(curriculumId);

ALTER TABLE Curriculum ADD CONSTRAINT fk_Currculum_Decision foreign key(decisionId) references Decision(decisionId);

ALTER TABLE Assessment ADD CONSTRAINT fk_Assessment_Subject foreign key(subjectCode) references Subject(subjectCode);

ALTER TABLE Question ADD CONSTRAINT fk_Question_Session foreign key(sessionId) references Session(sessionId);

ALTER TABLE Session ADD CONSTRAINT fk_Session_Subject foreign key(subjectCode) references Subject(subjectCode);

ALTER TABLE Material ADD CONSTRAINT fk_Material_Subject foreign key(subjectCode) references Subject(subjectCode);

ALTER TABLE Syllabus
ADD CONSTRAINT fk_Syllabus_Subject foreign key(subjectCode) references Subject(subjectCode), 
ADD CONSTRAINT fk_Syllabus_Decision foreign key(decisionId) references Decision(decisionId);

ALTER TABLE Account ADD CONSTRAINT fk_Account_Role foreign key(roleId) references Role(roleId);

